sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var role = new GlideRecord('sys_user_has_role');
	role.initialize();
	role.setValue('user', current.getValue('sys_id'));

	var rolle = new GlideRecord('sys_user_role');

	if (current.getValue('x_684800_g4_parkra_rolle') == 'Studierender'){
		rolle.addQuery('name', 'x_684800_g4_parkra.studierender');
		rolle.query();
		rolle.next();
		role.setValue('role', rolle.getValue('sys_id'));
	}
	else if (current.getValue('x_684800_g4_parkra_rolle') == 'Mitarbeiter'){
		rolle.addQuery('name', 'x_684800_g4_parkra.mitarbeiter');
		rolle.query();
		rolle.next();
		role.setValue('role', rolle.getValue('sys_id'));
	}
	else if (current.getValue('x_684800_g4_parkra_rolle') == 'Professor'){
		rolle.addQuery('name', 'x_684800_g4_parkra.professor');
		rolle.query();
		rolle.next();
		role.setValue('role', rolle.getValue('sys_id'));
	}
	else if (current.getValue('x_684800_g4_parkra_rolle') == 'Gast'){
		rolle.addQuery('name', 'x_684800_g4_parkra.gast');
		rolle.query();
		rolle.next();
		role.setValue('role', rolle.getValue('sys_id'));
	}
	else if (current.getValue('x_684800_g4_parkra_rolle') == 'Externer'){
		rolle.addQuery('name', 'x_684800_g4_parkra.externer');
		rolle.query();
		rolle.next();
		role.setValue('role', rolle.getValue('sys_id'));
	}

	role.insert();

})(current, previous);
