sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var parkzone = current.parkzone.getRefRecord();
	var user = current.usernummer.getRefRecord();

	var rolle = user.getValue('rolle');

	if (rolle == "Studierender" || rolle == "Mitarbeiter" || rolle == "Professor"){
		current.setValue('beginn', '2021-09-01');
		current.setValue('ende', '2022-02-28');
		current.update();
	}
	else if (rolle == "Externer"){
		var date = new GlideDate();
		current.setValue('beginn', date.getByFormat('yyyy-mm-dd'));
		current.setValue('ende', date.getByFormat('yyyy-mm-dd'));
		var rechnung = user.getValue('rechnungssumme');
		rechnung = parseInt(rechnung, 10) + parseInt(parkzone.getValue('preis'), 10);
		user.setValue('rechnungssumme', rechnung);
		current.update();
		user.update();
	}

	var behindert = current.getValue('behinderung');
	gs.info(behindert);
	var bparkplaetze = parkzone.getValue('behindertenparkplaetze');
	var parkplaetze = parkzone.getValue('kapazitat');

	if (parseInt(parkplaetze, 10) > 0){
		if (behindert == true){
			if (parseInt(bparkplaetze, 10) > 0){
				var res = parseInt(bparkplaetze, 10) - 1;
				parkzone.setValue('behindertenparkplaetze', res);
				parkzone.update();
			}		
			else{
				gs.info('Es ist leider kein Behindertenparkplatz mehr verfügbar, aber es wurde ein normaler Parkplatz gebucht.');
			}
		}
		var res1 = parseInt(parkplaetze, 10) - 1;
		parkzone.setValue('kapazitat', res1);
		parkzone.update();
	}
	else{
		gs.info('Es ist leider kein Parkplatz mehr frei, versuchen Sie es eventuell auf einer anderen Stellfläche.');
	}
})(current, previous);
