sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
   g_form.getReference('usernummer', doStuff);
}

function doStuff(caller) { // reference is passed into callback as first arguments
	var rolle = caller.getValue('x_684800_g4_parkra_rolle');

	if (rolle == 'Gast'){
		g_form.setDisplay('ende', true);
		g_form.setDisplay('beginn', true);
	}
	else{
		g_form.setDisplay('ende', false);
		g_form.setDisplay('beginn', false);
	}

	if (rolle == 'Professor' || rolle == 'Gast'){
		g_form.setReadOnly('usernummer', false);
	}
	else{
		g_form.setReadOnly('usernummer', true);
	}

	if (g_user.hasRoleExactly('x_684800_g4_parkra.administrator')){
		g_form.setDisplay('approved', true);
	}
	else{
		g_form.setDisplay('approved', false);
	}
}
